mapper.delete:
DELETE FROM 
STATION_BOARD
WHERE
SCODE = #{scode} 
AND ENTRYNUM=#{entryNum}

mapper.update:
UPDATE STATION_BOARD
SET
TITLE=#{title},CONTENT=#{content}

,IMGPATH=#{imgPath}

WHERE
SCODE = #{scode} 
AND ENTRYNUM = #{entryNum}

UPDATE STATION_BOARD
SET
VIEWCOUNT=VIEWCOUNT+1
WHERE
SCODE = #{scode} 
AND ENTRYNUM = #{entryNum}
